<?php
// app/Console/Commands/ImportStaticCaseStudies.php

namespace App\Console\Commands;

use Illuminate\Console\Command;
use App\Models\CaseStudy;
use App\Models\Category;
use Illuminate\Support\Facades\DB;

class ImportStaticCaseStudies extends Command
{
    protected $signature = 'casestudies:import {--force : Force import even if case studies exist}';
    protected $description = 'Import static case studies from hardcoded data';

    public function handle()
    {
        // Check if data already exists
        if (CaseStudy::count() > 0 && !$this->option('force')) {
            if (!$this->confirm('Case studies already exist in database. Do you want to continue?')) {
                $this->info('Import cancelled.');
                return 0;
            }
        }

        $this->info('Starting case studies import...');
        
        DB::beginTransaction();
        
        try {
            // Create categories first
            $this->createCategories();
            
            // Import all case studies
            $caseStudies = $this->getCaseStudiesData();
            $bar = $this->output->createProgressBar(count($caseStudies));
            $bar->start();
            
            foreach ($caseStudies as $csData) {
                $this->importCaseStudy($csData);
                $bar->advance();
            }
            
            $bar->finish();
            $this->newLine();
            
            DB::commit();
            
            $this->info('✅ Successfully imported ' . count($caseStudies) . ' case studies!');
            $this->table(
                ['Metric', 'Count'],
                [
                    ['Case Studies', CaseStudy::count()],
                    ['Categories', Category::count()],
                    ['Total Technologies', DB::table('technologies')->count()],
                    ['Total Timeline Items', DB::table('timelines')->count()],
                    ['Total Metrics', DB::table('metrics')->count()],
                ]
            );
            
            return 0;
            
        } catch (\Exception $e) {
            DB::rollBack();
            $this->error('❌ Import failed: ' . $e->getMessage());
            return 1;
        }
    }

    private function createCategories()
    {
        $categories = [
            ['name' => 'Cloud', 'slug' => 'cloud'],
            ['name' => 'Security', 'slug' => 'security'],
            ['name' => 'DevOps', 'slug' => 'devops'],
            ['name' => 'Infrastructure', 'slug' => 'infrastructure'],
            ['name' => 'Consulting', 'slug' => 'consulting'],
            ['name' => 'AI & ML', 'slug' => 'ai'],
            ['name' => 'Data', 'slug' => 'data'],
            ['name' => 'Migration', 'slug' => 'migration'],
        ];

        foreach ($categories as $cat) {
            Category::firstOrCreate(['slug' => $cat['slug']], $cat);
        }
    }

    private function importCaseStudy($csData)
    {
        // Check if case study already exists by title
        $existing = CaseStudy::where('title', $csData['title'])->first();
        if ($existing) {
            $this->newLine();
            $this->warn("⚠️  Skipping duplicate: {$csData['title']}");
            return;
        }

        $caseStudy = CaseStudy::create([
            'title' => $csData['title'],
            'description' => $csData['description'],
            'image' => $csData['image'],
            'search_keywords' => $csData['searchKeywords'] ?? '',
            'challenge' => $csData['challenge'] ?? null,
            'solution' => $csData['solution'] ?? null,
            'results' => $csData['results'] ?? null,
        ]);

        // Attach categories
        $categoryIds = Category::whereIn('slug', $csData['categories'])->pluck('id');
        $caseStudy->categories()->attach($categoryIds);

        // Create technologies
        if (isset($csData['technologies'])) {
            foreach ($csData['technologies'] as $index => $tech) {
                $caseStudy->technologies()->create([
                    'name' => $tech['name'],
                    'icon' => $tech['icon'],
                    'order' => $index,
                ]);
            }
        }

        // Create timeline
        if (isset($csData['timeline'])) {
            foreach ($csData['timeline'] as $index => $item) {
                $caseStudy->timeline()->create([
                    'date' => $item['date'],
                    'title' => $item['title'],
                    'description' => $item['description'],
                    'order' => $index,
                ]);
            }
        }

        // Create metrics
        if (isset($csData['metrics'])) {
            foreach ($csData['metrics'] as $index => $metric) {
                $caseStudy->metrics()->create([
                    'number' => $metric['number'],
                    'label' => $metric['label'],
                    'order' => $index,
                ]);
            }
        }
    }

