<?php

namespace App\Http\Controllers\Admin;

use App\Http\Controllers\Controller;
use App\Models\Blog;
use App\Enums\BlogStatus;
use Illuminate\Http\Request;
use Illuminate\Support\Str;
use Illuminate\Support\Facades\Storage;

class BlogController extends Controller
{
    public function index()
    {
        $blogs = Blog::latest()->get();
        return view('admin.blogs.index', compact('blogs'));
    }

    public function create()
    {
        $categories = ['Technology', 'Business', 'Marketing', 'Development', 'Cloud', 'Security'];
        return view('admin.blogs.create', compact('categories'));
    }

    public function store(Request $request)
    {
        $request->validate([
            'title' => 'required|string|max:255',
            'category' => 'required|string|max:255',
            'status' => 'required|in:draft,published',
            'published_at' => 'nullable|date',
            'excerpt' => 'required|string|max:500',
            'featured_image' => 'required|image|mimes:jpg,jpeg,png,gif,webp|max:2048',
            'content' => 'required|string',
            'author' => 'required|string|max:255',
            'tags' => 'nullable|string',
            'seo_title' => 'nullable|string|max:255',
            'seo_description' => 'nullable|string|max:500',
            'is_featured' => 'boolean',
        ]);

        $imagePath = null;
        if ($request->hasFile('featured_image')) {
            $imagePath = $request->file('featured_image')->store('blogs', 'public');
        }

        Blog::create([
            'title' => $request->title,
            'slug' => Str::slug($request->title),
            'category' => $request->category,
            'status' => $request->status,
            'published_at' => $request->status === 'published' ? ($request->published_at ?? now()) : null,
            'excerpt' => $request->excerpt,
            'featured_image' => $imagePath,
            'content' => $request->content,
            'author' => $request->author,
            'tags' => $request->tags,
            'seo_title' => $request->seo_title ?? $request->title,
            'seo_description' => $request->seo_description ?? $request->excerpt,
            'is_featured' => $request->has('is_featured'),
        ]);

        return redirect()->route('admin.blogs.index')->with('success', 'Blog created successfully.');
    }

    public function edit(Blog $blog)
    {
        $categories = ['Technology', 'Business', 'Marketing', 'Development', 'Cloud', 'Security'];
        return view('admin.blogs.edit', compact('blog', 'categories'));
    }

    public function update(Request $request, Blog $blog)
    {
        $request->validate([
            'title' => 'required|string|max:255',
            'category' => 'required|string|max:255',
            'status' => 'required|in:draft,published',
            'published_at' => 'nullable|date',
            'excerpt' => 'required|string|max:500',
            'featured_image' => 'sometimes|image|mimes:jpg,jpeg,png,gif,webp|max:2048',
            'content' => 'required|string',
            'author' => 'required|string|max:255',
            'tags' => 'nullable|string',
            'seo_title' => 'nullable|string|max:255',
            'seo_description' => 'nullable|string|max:500',
            'is_featured' => 'boolean',
        ]);

        $imagePath = $blog->featured_image;
        
        if ($request->hasFile('featured_image')) {
            if ($blog->featured_image) {
                Storage::disk('public')->delete($blog->featured_image);
            }
            $imagePath = $request->file('featured_image')->store('blogs', 'public');
        }

        $blog->update([
            'title' => $request->title,
            'slug' => Str::slug($request->title),
            'category' => $request->category,
            'status' => $request->status,
            'published_at' => $request->status === 'published' ? ($request->published_at ?? $blog->published_at ?? now()) : null,
            'excerpt' => $request->excerpt,
            'featured_image' => $imagePath,
            'content' => $request->content,
            'author' => $request->author,
            'tags' => $request->tags,
            'seo_title' => $request->seo_title ?? $request->title,
            'seo_description' => $request->seo_description ?? $request->excerpt,
            'is_featured' => $request->has('is_featured'),
        ]);

        return redirect()->route('admin.blogs.index')->with('success', 'Blog updated successfully.');
    }

    public function destroy(Blog $blog)
    {
        if ($blog->featured_image) {
            Storage::disk('public')->delete($blog->featured_image);
        }
        
        $blog->delete();
        return redirect()->route('admin.blogs.index')->with('success', 'Blog deleted successfully.');
    }
}
