<?php

namespace App\Http\Controllers;

use Illuminate\Http\Request;
use Illuminate\Support\Facades\Mail;

class ContactController extends Controller
{
    public function show()
    {
        return view('contact'); // resources/views/contact.blade.php
    }

    public function submit(Request $request)
    {
        $data = $request->validate([
            'first_name' => ['required', 'string', 'max:80'],
            'last_name' => ['required', 'string', 'max:80'],
            'email' => ['required', 'email', 'max:120'],
            'cc' => ['nullable', 'string', 'max:6'],
            'phone' => ['nullable', 'string', 'max:30'],
            'company' => ['nullable', 'string', 'max:120'],
            'message' => ['required', 'string', 'max:3000'],
        ]);

        // (Optional) mail bhejna â€“ simple raw mail:
        try {
            Mail::raw(
                "New contact:\n\n" .
                "Name: {$data['first_name']} {$data['last_name']}\n" .
                "Email: {$data['email']}\n" .
                "Phone: " . (($data['cc'] ?? '') . ' ' . ($data['phone'] ?? '')) . "\n" .
                "Company: " . ($data['company'] ?? '') . "\n\n" .
                "Message:\n{$data['message']}",
                function ($m) use ($data) {
                    $m->to('support@technophilesden.com', 'Technophiles Den')
                        ->subject('New contact form submission');
                }
            );
        } catch (\Throwable $e) {
            // logging etc.
        }

        return back()->with('status', 'Thanks! We received your message and will get back soon.');
    }
}
