<?php

namespace App\Models;

use Illuminate\Database\Eloquent\Factories\HasFactory;
use Illuminate\Database\Eloquent\Model;

class CaseStudy extends Model
{
    use HasFactory;

    /**
     * The table associated with the model.
     *
     * @var string
     */
    protected $table = 'case_studies';

    /**
     * The attributes that are mass assignable.
     *
     * @var array
     */
    protected $fillable = [
        'title',
        'description',
        'image',
        'search_keywords',
        'challenge',
        'solution',
        'results',
        'project_date',
        'status',
        'is_featured',
        'display_order'
    ];

    /**
     * The attributes that should be cast.
     *
     * @var array
     */
    protected $casts = [
        'is_featured' => 'boolean',
        'created_at' => 'datetime',
        'updated_at' => 'datetime',
    ];

    /**
     * Get the categories for the case study.
     */
    public function categories()
    {
        return $this->belongsToMany(
            CaseStudyCategory::class,
            'case_study_category_pivot',
            'case_study_id',
            'case_study_category_id'
        );
    }

    /**
     * Get the technologies for the case study.
     */
    public function technologies()
    {
        return $this->hasMany(CaseStudyTechnology::class)->orderBy('display_order');
    }

    /**
     * Get the timeline items for the case study.
     */
    public function timeline()
    {
        return $this->hasMany(CaseStudyTimeline::class)->orderBy('display_order');
    }

    /**
     * Get the metrics for the case study.
     */
    public function metrics()
    {
        return $this->hasMany(CaseStudyMetric::class)->orderBy('display_order');
    }

    /**
     * Scope a query to only include published case studies.
     */
    public function scopePublished($query)
    {
        return $query->where('status', 'published');
    }

    /**
     * Scope a query to only include featured case studies.
     */
    public function scopeFeatured($query)
    {
        return $query->where('is_featured', true);
    }

    /**
     * Scope a query to order by display order.
     */
    public function scopeOrdered($query)
    {
        return $query->orderBy('display_order')->orderBy('created_at', 'desc');
    }
}
