// ===== ADVANCED CRM PAGE INTERACTIONS =====

document.addEventListener('DOMContentLoaded', function() {
    
    // Initialize AOS (Animate On Scroll)
    AOS.init({
        duration: 800,
        easing: 'ease-out-cubic',
        once: true,
        offset: 50,
        delay: 100
    });

    // ===== BILLING TOGGLE FUNCTIONALITY =====
    const billingToggle = document.getElementById('billingToggle');
    const priceElements = document.querySelectorAll('.price-counter');
    const billingPeriodElements = document.querySelectorAll('.billing-period');
    const monthlyLabel = document.getElementById('monthlyLabel');
    const yearlyLabel = document.getElementById('yearlyLabel');

    // Price counter animation function
    const animatePrice = (element, target, duration = 500) => {
        let startTime = null;
        const startValue = parseInt(element.textContent) || 0;

        const animation = (currentTime) => {
            if (!startTime) startTime = currentTime;
            const progress = Math.min((currentTime - startTime) / duration, 1);
            
            const currentValue = Math.floor(progress * (target - startValue) + startValue);
            element.textContent = currentValue;
            
            if (progress < 1) {
                requestAnimationFrame(animation);
            } else {
                element.textContent = target;
            }
        };

        requestAnimationFrame(animation);
    };

    if (billingToggle) {
        billingToggle.addEventListener('change', function() {
            const isYearly = this.checked;
            
            // Update labels with smooth transition
            if (isYearly) {
                monthlyLabel.classList.remove('active');
                yearlyLabel.classList.add('active');
            } else {
                monthlyLabel.classList.add('active');
                yearlyLabel.classList.remove('active');
            }

            // Animate price changes
            priceElements.forEach(price => {
                const monthlyPrice = parseInt(price.getAttribute('data-monthly'));
                const yearlyPrice = parseInt(price.getAttribute('data-yearly'));
                const targetPrice = isYearly ? yearlyPrice : monthlyPrice;
                
                // Smooth price transition
                animatePrice(price, targetPrice, 500);
            });

            // Update billing period text
            billingPeriodElements.forEach(period => {
                period.textContent = isYearly ? 'annually' : 'monthly';
            });
        });
    }

    // Initialize monthly as active
    if (monthlyLabel) {
        monthlyLabel.classList.add('active');
    }

    // ===== FAQ ACCORDION WITH SMOOTH ANIMATION =====
    const faqItems = document.querySelectorAll('.faq-item');
    
    faqItems.forEach(item => {
        const question = item.querySelector('.faq-question');
        
        question.addEventListener('click', () => {
            const isActive = item.classList.contains('active');
            
            // Close all FAQ items with animation
            faqItems.forEach(faq => {
                faq.classList.remove('active');
            });
            
            // Open clicked item if it wasn't active
            if (!isActive) {
                item.classList.add('active');
            }
        });
    });

    // ===== CARD GLOW EFFECT ON MOUSE MOVE =====
    const cards = document.querySelectorAll('.crm-type-card, .pricing-card');
    
    cards.forEach(card => {
        const glow = card.querySelector('.card-glow, .pricing-glow');
        
        if (glow) {
            card.addEventListener('mousemove', (e) => {
                const rect = card.getBoundingClientRect();
                const x = ((e.clientX - rect.left) / rect.width) * 100;
                const y = ((e.clientY - rect.top) / rect.height) * 100;
                
                card.style.setProperty('--x', `${x}%`);
                card.style.setProperty('--y', `${y}%`);
            });
        }
    });

    // ===== SMOOTH SCROLL FOR ANCHOR LINKS =====
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            const href = this.getAttribute('href');
            if (href !== '#') {
                e.preventDefault();
                const target = document.querySelector(href);
                if (target) {
                    const offsetTop = target.offsetTop - 80; // Account for header
                    window.scrollTo({
                        top: offsetTop,
                        behavior: 'smooth'
                    });
                }
            }
        });
    });

    // ===== PARALLAX EFFECT FOR HERO SECTION =====
  // ===== PARALLAX EFFECT FOR HERO SECTION (FIXED) =====
const hero = document.querySelector('.crm-hero');
const heroContent = document.querySelector('.crm-hero-content');

if (hero && heroContent) {
    window.addEventListener('scroll', () => {
        const scrolled = window.pageYOffset;
        const parallaxSpeed = 0.1;
        const heroHeight = hero.offsetHeight;
        
        // Only apply parallax within hero section
        if (scrolled < heroHeight) {
            heroContent.style.transform = `translateY(${scrolled * parallaxSpeed}px)`;
            // Remove opacity fade to prevent content from disappearing
            heroContent.style.opacity = 1;
        } else {
            // Reset transform when past hero section
            heroContent.style.transform = 'translateY(0)';
            heroContent.style.opacity = 1;
        }
    });
}


    // ===== REVEAL ANIMATION FOR TABLE ROWS =====
    const tableRows = document.querySelectorAll('.table-row-hover');
    
    const tableObserver = new IntersectionObserver((entries) => {
        entries.forEach((entry, index) => {
            if (entry.isIntersecting) {
                setTimeout(() => {
                    entry.target.style.opacity = '1';
                    entry.target.style.transform = 'translateX(0)';
                }, index * 50);
                tableObserver.unobserve(entry.target);
            }
        });
    }, { threshold: 0.1 });

    tableRows.forEach(row => {
        row.style.opacity = '0';
        row.style.transform = 'translateX(-20px)';
        row.style.transition = 'all 0.5s ease-out';
        tableObserver.observe(row);
    });

    // ===== RIPPLE EFFECT ON BUTTONS =====
    const rippleButtons = document.querySelectorAll('.ripple-effect');
    
    rippleButtons.forEach(button => {
        button.addEventListener('click', function(e) {
            const ripple = document.createElement('span');
            const rect = this.getBoundingClientRect();
            const size = Math.max(rect.width, rect.height);
            const x = e.clientX - rect.left - size / 2;
            const y = e.clientY - rect.top - size / 2;
            
            ripple.style.width = ripple.style.height = size + 'px';
            ripple.style.left = x + 'px';
            ripple.style.top = y + 'px';
            ripple.style.position = 'absolute';
            ripple.style.borderRadius = '50%';
            ripple.style.background = 'rgba(255, 255, 255, 0.5)';
            ripple.style.pointerEvents = 'none';
            ripple.style.animation = 'ripple-animation 0.6s ease-out';
            ripple.classList.add('ripple');
            
            this.appendChild(ripple);
            
            setTimeout(() => ripple.remove(), 600);
        });
    });

    // Add ripple animation CSS dynamically
    const style = document.createElement('style');
    style.textContent = `
        @keyframes ripple-animation {
            from {
                transform: scale(0);
                opacity: 1;
            }
            to {
                transform: scale(4);
                opacity: 0;
            }
        }
    `;
    document.head.appendChild(style);

    // ===== PRICING CARD FEATURE REVEAL ON HOVER =====
    const pricingCards = document.querySelectorAll('.pricing-card');
    
    pricingCards.forEach(card => {
        card.addEventListener('mouseenter', function() {
            const features = this.querySelectorAll('.feature-reveal');
            features.forEach((feature, index) => {
                feature.style.animation = `featureReveal 0.3s ease-out ${index * 0.05}s forwards`;
            });
        });
    });

    // ===== SCROLL PROGRESS INDICATOR =====
    const createScrollProgress = () => {
        const progressBar = document.createElement('div');
        progressBar.className = 'scroll-progress';
        progressBar.style.cssText = `
            position: fixed;
            top: 0;
            left: 0;
            height: 3px;
            background: linear-gradient(90deg, white, gray);
            z-index: 9999;
            transition: width 0.1s ease-out;
        `;
        document.body.appendChild(progressBar);

        window.addEventListener('scroll', () => {
            const scrollPercent = (window.pageYOffset / (document.body.scrollHeight - window.innerHeight)) * 100;
            progressBar.style.width = scrollPercent + '%';
        });
    };

    createScrollProgress();

    // ===== TYPING EFFECT FOR HERO TITLE =====
    const typingElement = document.querySelector('.typing-effect');
    if (typingElement) {
        const text = typingElement.textContent;
        typingElement.textContent = '';
        let charIndex = 0;

        const typeChar = () => {
            if (charIndex < text.length) {
                typingElement.textContent += text.charAt(charIndex);
                charIndex++;
                setTimeout(typeChar, 100);
            }
        };

        // Start typing after a delay
        setTimeout(typeChar, 1000);
    }

    // ===== INTERSECTION OBSERVER FOR ICON ANIMATIONS =====
    const animateElements = document.querySelectorAll('.card-icon, .plan-icon');
    
    const iconObserver = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.style.animation = 'iconFloat 3s ease-in-out infinite';
            }
        });
    }, { threshold: 0.5 });

    animateElements.forEach(el => iconObserver.observe(el));

    // ===== HERO STAT ITEMS HOVER EFFECT =====
    const statItems = document.querySelectorAll('.stat-item');
    
    statItems.forEach(item => {
        item.addEventListener('mouseenter', function() {
            this.style.transform = 'translateY(-10px) scale(1.05)';
        });
        
        item.addEventListener('mouseleave', function() {
            this.style.transform = 'translateY(0) scale(1)';
        });
    });

    // ===== CRM TYPE CARDS - 3D TILT EFFECT =====
    const crmCards = document.querySelectorAll('.crm-type-card');
    
    crmCards.forEach(card => {
        card.addEventListener('mousemove', (e) => {
            const rect = card.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            
            const centerX = rect.width / 2;
            const centerY = rect.height / 2;
            
            const rotateX = (y - centerY) / 10;
            const rotateY = (centerX - x) / 10;
            
            card.style.transform = `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) translateY(-10px) scale(1.02)`;
        });
        
        card.addEventListener('mouseleave', () => {
            card.style.transform = 'perspective(1000px) rotateX(0) rotateY(0) translateY(0) scale(1)';
        });
    });

    // ===== PRICING CARDS - 3D TILT EFFECT =====
    pricingCards.forEach(card => {
        card.addEventListener('mousemove', (e) => {
            const rect = card.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            
            const centerX = rect.width / 2;
            const centerY = rect.height / 2;
            
            const rotateX = (y - centerY) / 15;
            const rotateY = (centerX - x) / 15;
            
            card.style.transform = `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) translateY(-10px) scale(1.02)`;
        });
        
        card.addEventListener('mouseleave', () => {
            card.style.transform = 'perspective(1000px) rotateX(0) rotateY(0) translateY(0) scale(1)';
        });
    });

    // ===== PERFORMANCE: LAZY LOADING FOR ANIMATIONS =====
    const lazyAnimations = () => {
        const elements = document.querySelectorAll('[data-aos]');
        elements.forEach(el => {
            if (el.getBoundingClientRect().top < window.innerHeight) {
                el.classList.add('aos-animate');
            }
        });
    };

    window.addEventListener('scroll', debounce(lazyAnimations, 100), { passive: true });

    // ===== ENTERPRISE CTA HOVER EFFECT =====
    const enterpriseCta = document.querySelector('.enterprise-cta');
    
    if (enterpriseCta) {
        enterpriseCta.addEventListener('mouseenter', function() {
            this.style.transform = 'translateY(-5px) scale(1.02)';
        });
        
        enterpriseCta.addEventListener('mouseleave', function() {
            this.style.transform = 'translateY(0) scale(1)';
        });
    }

    // ===== COMPARISON TABLE COLUMN HIGHLIGHT =====
    const tableHeaders = document.querySelectorAll('.comparison-table th.table-hover-col');
    const tableCells = document.querySelectorAll('.comparison-table td');
    
    tableHeaders.forEach((header, index) => {
        header.addEventListener('mouseenter', function() {
            // Highlight entire column
            const columnIndex = Array.from(this.parentElement.children).indexOf(this);
            document.querySelectorAll(`.comparison-table tbody tr`).forEach(row => {
                const cell = row.children[columnIndex];
                if (cell) {
                    cell.style.background = 'var(--gray-900)';
                    cell.style.color = 'var(--white)';
                }
            });
        });
        
        header.addEventListener('mouseleave', function() {
            // Remove highlight
            const columnIndex = Array.from(this.parentElement.children).indexOf(this);
            document.querySelectorAll(`.comparison-table tbody tr`).forEach(row => {
                const cell = row.children[columnIndex];
                if (cell) {
                    cell.style.background = '';
                    cell.style.color = '';
                }
            });
        });
    });

    // ===== ANIMATE CHECK/CROSS ICONS IN TABLE =====
    const checkIcons = document.querySelectorAll('.check-animated, .cross-animated');
    
    const checkObserver = new IntersectionObserver((entries) => {
        entries.forEach((entry, index) => {
            if (entry.isIntersecting) {
                setTimeout(() => {
                    entry.target.style.animation = entry.target.classList.contains('check-animated') 
                        ? 'checkBounce 0.5s ease-out' 
                        : 'crossShake 0.5s ease-out';
                }, index * 50);
                checkObserver.unobserve(entry.target);
            }
        });
    }, { threshold: 0.5 });

    checkIcons.forEach(icon => checkObserver.observe(icon));

    // ===== ADD CROSS SHAKE ANIMATION =====
    const crossStyle = document.createElement('style');
    crossStyle.textContent = `
        @keyframes crossShake {
            0%, 100% { transform: rotate(0deg); }
            25% { transform: rotate(-10deg); }
            75% { transform: rotate(10deg); }
        }
    `;
    document.head.appendChild(crossStyle);

    // ===== TRUST BADGES ANIMATION =====
    const trustBadges = document.querySelectorAll('.badge-item');
    
    const badgeObserver = new IntersectionObserver((entries) => {
        entries.forEach((entry, index) => {
            if (entry.isIntersecting) {
                setTimeout(() => {
                    entry.target.style.opacity = '1';
                    entry.target.style.transform = 'translateY(0)';
                }, index * 100);
                badgeObserver.unobserve(entry.target);
            }
        });
    }, { threshold: 0.5 });

    trustBadges.forEach(badge => {
        badge.style.opacity = '0';
        badge.style.transform = 'translateY(20px)';
        badge.style.transition = 'all 0.5s ease-out';
        badgeObserver.observe(badge);
    });

    // ===== SCROLL TO TOP BUTTON =====
    const createScrollToTop = () => {
        const button = document.createElement('button');
        button.innerHTML = '<i class="fas fa-arrow-up"></i>';
        button.className = 'scroll-to-top';
        button.style.cssText = `
            position: fixed;
            bottom: 30px;
            right: 30px;
            width: 50px;
            height: 50px;
            background: var(--white);
            color: var(--black);
            border: 2px solid var(--white);
            cursor: pointer;
            display: none;
            align-items: center;
            justify-content: center;
            z-index: 9998;
            transition: all 0.3s ease;
            font-size: 20px;
        `;
        
        button.addEventListener('click', () => {
            window.scrollTo({
                top: 0,
                behavior: 'smooth'
            });
        });
        
        button.addEventListener('mouseenter', () => {
            button.style.background = 'var(--black)';
            button.style.color = 'var(--white)';
            button.style.transform = 'translateY(-5px)';
        });
        
        button.addEventListener('mouseleave', () => {
            button.style.background = 'var(--white)';
            button.style.color = 'var(--black)';
            button.style.transform = 'translateY(0)';
        });
        
        document.body.appendChild(button);
        
        window.addEventListener('scroll', () => {
            if (window.pageYOffset > 500) {
                button.style.display = 'flex';
            } else {
                button.style.display = 'none';
            }
        });
    };

    createScrollToTop();

    console.log('ðŸš€ CRM Page Initialized with Advanced Animations');
});

// ===== UTILITY: DEBOUNCE FUNCTION =====
function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
        const later = () => {
            clearTimeout(timeout);
            func(...args);
        };
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
    };
}

// ===== UTILITY: THROTTLE FUNCTION =====
function throttle(func, limit) {
    let inThrottle;
    return function() {
        const args = arguments;
        const context = this;
        if (!inThrottle) {
            func.apply(context, args);
            inThrottle = true;
            setTimeout(() => inThrottle = false, limit);
        }
    };
}
