
        document.addEventListener("DOMContentLoaded", () => {
            
            // ===== HERO ANIMATIONS =====
            const initHeroAnimations = () => {
                const heroContent = document.querySelector('.hrms-hero-content');
                const heroIllustration = document.querySelector('.hrms-illustration');
                const productHunt = document.querySelector('.hrms-product-hunt');
                
                setTimeout(() => {
                    if (heroContent) heroContent.classList.add('animate');
                }, 200);
                
                setTimeout(() => {
                    if (heroIllustration) heroIllustration.classList.add('animate');
                }, 400);
                
                setTimeout(() => {
                    if (productHunt) productHunt.classList.add('animate');
                }, 800);
            };

            // ===== INTERSECTION OBSERVER FOR ANIMATIONS =====
            const initScrollAnimations = () => {
                const observerOptions = {
                    threshold: 0.1,
                    rootMargin: '0px 0px -50px 0px'
                };

                const observer = new IntersectionObserver((entries) => {
                    entries.forEach(entry => {
                        if (entry.isIntersecting) {
                            entry.target.classList.add('animate');
                        }
                    });
                }, observerOptions);

                const animationElements = document.querySelectorAll(
                    '.hrms-feature-item, .hrms-testimonial-card, .hrms-news-card, .hrms-stat-card'
                );
                
                animationElements.forEach(el => {
                    observer.observe(el);
                });
            };

            // ===== DASHBOARD SIDEBAR FUNCTIONALITY =====
            const initDashboardSidebar = () => {
                const navItems = document.querySelectorAll('.hrms-nav-item');
                const mainHeader = document.querySelector('.hrms-main-header h1');
                
                const sectionContent = {
                    dashboard: 'Hello Eren!',
                    'job-postings': 'Job Postings Management',
                    candidates: 'Candidates Management', 
                    'application-board': 'Application Board',
                    tags: 'Tags & Categories'
                };

                navItems.forEach((item) => {
                    item.addEventListener('click', () => {
                        // Remove active from all
                        navItems.forEach(nav => nav.classList.remove('active'));
                        // Add active to clicked
                        item.classList.add('active');
                        
                        // Update header
                        const section = item.getAttribute('data-section');
                        if (mainHeader && sectionContent[section]) {
                            mainHeader.textContent = sectionContent[section];
                        }
                    });
                });
            };

            // ===== FULLSCREEN FUNCTIONALITY =====
            const initFullscreen = () => {
                const fullscreenBtn = document.querySelector('[data-action="fullscreen"]');
                const browserFrame = document.querySelector('.hrms-browser-frame');
                
                if (fullscreenBtn && browserFrame) {
                    fullscreenBtn.addEventListener('click', () => {
                        // Create fullscreen overlay
                        const overlay = document.createElement('div');
                        overlay.style.cssText = `
                            position: fixed; top: 0; left: 0; width: 100%; height: 100%;
                            background: rgba(0, 0, 0, 0.9); z-index: 9999; display: flex;
                            align-items: center; justify-content: center; padding: 20px;
                            opacity: 0; transition: all 0.3s ease;
                        `;
                        
                        const clonedFrame = browserFrame.cloneNode(true);
                        clonedFrame.style.cssText = `
                            width: 100%; height: 100%; max-width: 1600px; max-height: 900px;
                            transform: scale(0.9); transition: transform 0.3s ease;
                        `;
                        
                        overlay.appendChild(clonedFrame);
                        document.body.appendChild(overlay);
                        document.body.style.overflow = 'hidden';
                        
                        // Add close button
                        const closeBtn = clonedFrame.querySelector('[data-action="fullscreen"]');
                        if (closeBtn) {
                            closeBtn.innerHTML = 'âœ•';
                            closeBtn.addEventListener('click', () => {
                                overlay.style.opacity = '0';
                                document.body.style.overflow = '';
                                setTimeout(() => overlay.remove(), 300);
                            });
                        }
                        
                        // Animate in
                        setTimeout(() => {
                            overlay.style.opacity = '1';
                            clonedFrame.style.transform = 'scale(1)';
                        }, 10);
                        
                        // ESC to close
                        const handleEscape = (e) => {
                            if (e.key === 'Escape') {
                                closeBtn.click();
                                document.removeEventListener('keydown', handleEscape);
                            }
                        };
                        document.addEventListener('keydown', handleEscape);
                    });
                }
            };

            // ===== BUTTON INTERACTIONS =====
            const initButtonInteractions = () => {
                // Add click effects to all buttons
                const buttons = document.querySelectorAll('button, [data-action]');
                
                buttons.forEach(button => {
                    button.addEventListener('click', function(e) {
                        // Create ripple effect
                        const ripple = document.createElement('span');
                        const rect = this.getBoundingClientRect();
                        const size = Math.max(rect.width, rect.height);
                        const x = e.clientX - rect.left - size / 2;
                        const y = e.clientY - rect.top - size / 2;
                        
                        ripple.style.cssText = `
                            position: absolute; border-radius: 50%;
                            background: rgba(255, 255, 255, 0.6);
                            transform: scale(0); animation: ripple 0.6s linear;
                            width: ${size}px; height: ${size}px;
                            left: ${x}px; top: ${y}px; pointer-events: none;
                        `;
                        
                        this.style.position = 'relative';
                        this.style.overflow = 'hidden';
                        this.appendChild(ripple);
                        
                        setTimeout(() => ripple.remove(), 600);
                        
                        // Handle specific actions
                        const action = this.getAttribute('data-action');
                        if (action) {
                            console.log(`Action triggered: ${action}`);
                            // Add your action handlers here
                        }
                    });
                });
                
                // Add ripple animation CSS if not exists
                if (!document.querySelector('#ripple-animation')) {
                    const style = document.createElement('style');
                    style.id = 'ripple-animation';
                    style.textContent = `
                        @keyframes ripple {
                            to { transform: scale(4); opacity: 0; }
                        }
                    `;
                    document.head.appendChild(style);
                }
            };

            // ===== SMOOTH SCROLLING =====
            const initSmoothScrolling = () => {
                const links = document.querySelectorAll('a[href^="#"]');
                
                links.forEach(link => {
                    link.addEventListener('click', (e) => {
                        e.preventDefault();
                        const targetId = link.getAttribute('href');
                        const targetElement = document.querySelector(targetId);
                        
                        if (targetElement) {
                            targetElement.scrollIntoView({
                                behavior: 'smooth',
                                block: 'start'
                            });
                        }
                    });
                });
            };

            // ===== PERFORMANCE OPTIMIZATIONS =====
            const initPerformance = () => {
                // Preload critical images
                const criticalImages = [
                    'img/hrms-hero-illustration.webp',
                    'img/jsik.webp',
                    'img/teacher.webp',
                    'img/pen.webp'
                ];
                
                criticalImages.forEach(src => {
                    const img = new Image();
                    img.src = src;
                });

                // Lazy load news images
                const newsImages = document.querySelectorAll('.hrms-news-image img');
                const imageObserver = new IntersectionObserver((entries) => {
                    entries.forEach(entry => {
                        if (entry.isIntersecting) {
                            const img = entry.target;
                            img.style.opacity = '1';
                            imageObserver.unobserve(img);
                        }
                    });
                });

                newsImages.forEach(img => {
                    img.style.opacity = '0';
                    img.style.transition = 'opacity 0.3s ease';
                    imageObserver.observe(img);
                });
            };

            // ===== ACCESSIBILITY =====
            const initAccessibility = () => {
                // Add keyboard navigation for custom elements
                const customClickables = document.querySelectorAll('.hrms-nav-item, [data-action]');
                
                customClickables.forEach(element => {
                    element.setAttribute('tabindex', '0');
                    element.setAttribute('role', 'button');
                    
                    element.addEventListener('keydown', (e) => {
                        if (e.key === 'Enter' || e.key === ' ') {
                            e.preventDefault();
                            element.click();
                        }
                    });
                });

                // Add ARIA labels
                const navItems = document.querySelectorAll('.hrms-nav-item');
                navItems.forEach((element) => {
                    const text = element.textContent.trim();
                    element.setAttribute('aria-label', `Navigate to ${text}`);
                });
            };

            // ===== ERROR HANDLING =====
            window.addEventListener('error', (e) => {
                console.error('JavaScript error:', e.error);
                
                const errorMsg = document.createElement('div');
                errorMsg.style.cssText = `
                    position: fixed; top: 20px; right: 20px;
                    background: #ef4444; color: white;
                    padding: 12px 20px; border-radius: 8px;
                    z-index: 10000; max-width: 300px;
                `;
                errorMsg.textContent = 'Something went wrong. Please refresh the page.';
                document.body.appendChild(errorMsg);
                
                setTimeout(() => errorMsg.remove(), 5000);
            });

            // ===== INITIALIZE ALL =====
            initHeroAnimations();
            initScrollAnimations();
            initDashboardSidebar();
            initFullscreen();
            initButtonInteractions();
            initSmoothScrolling();
            initPerformance();
            initAccessibility();
            
            console.log('ðŸš€ HRMS Clean initialized successfully!');
        });
    