// small animation on load (optional)
window.addEventListener('load', () => {
  document.querySelectorAll('.card-float, .screen').forEach(el => {
    el.style.transition = 'transform .8s ease, opacity .8s ease';
    el.style.opacity = '0';
    el.style.transform += ' translateY(12px)';
    requestAnimationFrame(() => {
      el.style.opacity = '1';
      el.style.transform = el.style.transform.replace(' translateY(12px)', ' translateY(0)');
    });
  });
});