    border-top: none;
    display: flex;
    gap: 8px;
    align-items: center;
    flex-shrink: 0;
    height: 55px; /* Reduced from 60px */
}

#chatbot-input {
    flex: 1;
    border: 1px solid rgba(0, 229, 208, 0.3);
    border-radius: 22px;
    padding: 9px 16px; /* Reduced padding */
    font-size: 12px; /* Reduced from 13px */
    outline: none;
    transition: all 0.2s ease;
    background: #2a3f54;
    color: #e0e0e0;
}

.chatbot-send-btn {
    width: 38px; /* Reduced from 40px */
    height: 38px;
    border-radius: 50%;
    background: linear-gradient(135deg, #00E5D0 0%, #90F070 100%);
    border: none;
    color: #1a1a1a;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    flex-shrink: 0;
    box-shadow: 0 2px 8px rgba(0, 229, 208, 0.4);
}

/* Powered By - COMPACT */
.powered-by {
    padding: 8px; /* Reduced padding */
    text-align: center;
    font-size: 10px; /* Reduced from 11px */
    color: #999;
    background: transparent;
    border-top: 1px solid rgba(0, 229, 208, 0.2);
    flex-shrink: 0;
    height: 35px; /* Reduced from 40px */
}

/* Responsive */
@media (max-width: 480px) {
    .chatbot-window {
        width: calc(100vw - 30px);
        height: calc(100vh - 150px);
        max-height: 520px;
        bottom: 15px;
        right: 15px;
    }
    
    .chatbot-toggle {
        width: 80px;
        height: 80px;
    }
    
    .chatbot-quick-actions {
        grid-template-columns: 1fr;
    }
}


</style>


<script>
document.addEventListener('DOMContentLoaded', function() {
    const chatbotToggle = document.getElementById('chatbot-toggle');
    const chatbotWindow = document.getElementById('chatbot-window');
    const chatbotClose = document.getElementById('chatbot-close');
    const chatbotInput = document.getElementById('chatbot-input');
    const chatbotSend = document.getElementById('chatbot-send');
    const chatbotMessages = document.getElementById('chatbot-messages');
    const quickActionBtns = document.querySelectorAll('.quick-action-btn');
    const welcomeMessage = document.getElementById('welcome-message');

    let conversationHistory = [];
    let isProcessing = false;

    // Toggle chatbot window
    chatbotToggle.addEventListener('click', function() {
        chatbotWindow.classList.toggle('active');
        if (chatbotWindow.classList.contains('active')) {
            chatbotInput.focus();
        }
    });

    // Close chatbot
    chatbotClose.addEventListener('click', function() {
        chatbotWindow.classList.remove('active');
    });

    // Hide welcome message
    function hideWelcomeMessage() {
        if (welcomeMessage) {
            welcomeMessage.style.display = 'none';
        }
    }
// Send message
function sendMessage() {
    const message = chatbotInput.value.trim();
    
    // Debug log
    console.log('Sending message:', message);
    
    if (message === '' || isProcessing) {
        console.log('Message empty or processing');
        return;
    }

    isProcessing = true;
    hideWelcomeMessage();
    
    // Add user message
    addMessage(message, 'user');
    conversationHistory.push({ role: 'user', content: message });
    chatbotInput.value = '';

    // Show typing indicator
    const typingId = addTypingIndicator();

    // Call API with proper structure
    fetch('/api/chat', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'X-Requested-With': 'XMLHttpRequest'
        },
        body: JSON.stringify({
            message: message,  // Make sure this is being sent
            history: conversationHistory
        })
    })
    .then(response => {
        console.log('Response status:', response.status);
        return response.json();
    })
    .then(data => {
        console.log('Response data:', data);
        removeTypingIndicator(typingId);
        
        if (data.success && data.response) {
            addMessage(data.response, 'bot');
            conversationHistory.push({ role: 'assistant', content: data.response });
        } else {
            const errorMsg = data.error || 'Sorry, I couldn\'t process your request. Please try again.';
            addMessage(errorMsg, 'bot');
        }
        
        isProcessing = false;
    })
    .catch(error => {
        console.error('Fetch Error:', error);
        removeTypingIndicator(typingId);
        addMessage('Sorry, something went wrong. Please check your connection.', 'bot');
        isProcessing = false;
    });
}


    chatbotSend.addEventListener('click', sendMessage);
    chatbotInput.addEventListener('keypress', function(e) {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            sendMessage();
        }
    });

    // Quick actions
    quickActionBtns.forEach(btn => {
        btn.addEventListener('click', function() {
            const message = this.getAttribute('data-message');
            chatbotInput.value = message;
            sendMessage();
        });
    });

    // Add typing indicator
    function addTypingIndicator() {
        const uniqueId = Date.now();
        const typingDiv = document.createElement('div');
        typingDiv.className = 'message bot-message typing-indicator';
        typingDiv.id = 'typing-' + uniqueId;
        
        typingDiv.innerHTML = `
            <div class="message-avatar-small">
                <svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg">
                    <circle cx="310" cy="310" r="85" fill="url(#typingGradient${uniqueId})"/>
                    <ellipse cx="310" cy="295" rx="70" ry="38" fill="#1A3D4D"/>
                    <circle cx="285" cy="290" r="18" fill="#FFF"/>
                    <circle cx="285" cy="290" r="10" fill="#1A3D4D"/>
                    <circle cx="335" cy="290" r="18" fill="#FFF"/>
                    <circle cx="335" cy="290" r="10" fill="#1A3D4D"/>
                    <path d="M 295 320 Q 310 328 325 320" stroke="#FFF" stroke-width="4" fill="none"/>
                    <defs>
                        <linearGradient id="typingGradient${uniqueId}" x1="0%" y1="0%" x2="100%" y2="100%">
                            <stop offset="0%" style="stop-color:#00E5D0;stop-opacity:1" />
                            <stop offset="100%" style="stop-color:#90F070;stop-opacity:1" />
                        </linearGradient>
                    </defs>
                </svg>
            </div>
            <div class="message-content typing-dots">
                <span></span>
                <span></span>
                <span></span>
            </div>
        `;

        chatbotMessages.appendChild(typingDiv);
        chatbotMessages.scrollTop = chatbotMessages.scrollHeight;
        return typingDiv.id;
    }

    function removeTypingIndicator(id) {
        const indicator = document.getElementById(id);
        if (indicator) {
            indicator.remove();
        }
    }

    // Add message to chat
    function addMessage(text, type) {
        const uniqueId = Date.now();
        const messageDiv = document.createElement('div');
        messageDiv.className = `message ${type}-message`;

        if (type === 'bot') {
            messageDiv.innerHTML = `
                <div class="message-avatar-small">
                    <svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg">
                        <circle cx="310" cy="310" r="85" fill="url(#msgGradient${uniqueId})"/>
                        <ellipse cx="310" cy="295" rx="70" ry="38" fill="#1A3D4D"/>
                        <circle cx="285" cy="290" r="18" fill="#FFF"/>
                        <circle cx="285" cy="290" r="10" fill="#1A3D4D"/>
                        <circle cx="335" cy="290" r="18" fill="#FFF"/>
                        <circle cx="335" cy="290" r="10" fill="#1A3D4D"/>
                        <path d="M 295 320 Q 310 328 325 320" stroke="#FFF" stroke-width="4" fill="none"/>
                        <defs>
                            <linearGradient id="msgGradient${uniqueId}" x1="0%" y1="0%" x2="100%" y2="100%">
                                <stop offset="0%" style="stop-color:#00E5D0;stop-opacity:1" />
                                <stop offset="100%" style="stop-color:#90F070;stop-opacity:1" />
                            </linearGradient>
                        </defs>
                    </svg>
                </div>
                <div class="message-content">
                    <p>${escapeHtml(text)}</p>
                </div>
            `;
        } else {
            messageDiv.innerHTML = `
                <div class="message-content">
                    <p>${escapeHtml(text)}</p>
                </div>
            `;
        }

        chatbotMessages.appendChild(messageDiv);
        chatbotMessages.scrollTop = chatbotMessages.scrollHeight;
    }

    // Helper function
    function escapeHtml(text) {
        const div = document.createElement('div');
        div.textContent = text;
        return div.innerHTML;
    }
});
</script>
