    <script src="{{ asset('js/main.js') }}" defer></script>
    <script   defer>
        document.addEventListener('DOMContentLoaded', function () {
            // AOS-lite animation
            const animateOnScroll = () => {
                document.querySelectorAll('[data-aos]').forEach(el => {
                    const top = el.getBoundingClientRect().top;
                    if (top < window.innerHeight - 150) {
                        el.classList.add('aos-animate');
                    }
                });
            };
            animateOnScroll();
            window.addEventListener('scroll', animateOnScroll, { passive: true });

            // Input focus state
            document.querySelectorAll('.form-input').forEach(input => {
                const wrap = input.closest('.input-wrapper');
                const toggle = () => {
                    if (wrap) {
                        wrap.classList.toggle('focused', !!input.value || document.activeElement === input);
                    }
                };
                input.addEventListener('focus', toggle);
                input.addEventListener('blur', toggle);
                input.addEventListener('input', toggle);
                toggle();
            });

            // Submit button ripple effect
            const submitBtn = document.querySelector('.btn-submit');
            if (submitBtn) {
                submitBtn.addEventListener('click', function (e) {
                    const ripple = this.querySelector('.btn-ripple');
                    if (!ripple) return;
                    ripple.classList.add('active');
                    setTimeout(() => ripple.classList.remove('active'), 600);
                });
            }

            // Auto-hide flash message
            const flash = document.getElementById('flashSuccess');
            if (flash) {
                setTimeout(() => {
                    flash.style.opacity = '0';
                    setTimeout(() => flash.remove(), 300);
                }, 5000);
            }

            // Social link hover effect
            document.querySelectorAll('.social-link').forEach(link => {
                link.addEventListener('mouseenter', () => {
                    link.style.transform = 'translateY(-3px) scale(1.1)';
                });
                link.addEventListener('mouseleave', () => {
                    link.style.transform = 'translateY(0) scale(1)';
                });
            });

            // Info block hover effect
            document.querySelectorAll('.info-block').forEach(block => {
                block.addEventListener('mouseenter', () => {
                    const icon = block.querySelector('.ib-icon');
                    if (icon) {
                        icon.style.transform = 'scale(1.1) rotate(5deg)';
                    }
                });
                block.addEventListener('mouseleave', () => {
                    const icon = block.querySelector('.ib-icon');
                    if (icon) {
                        icon.style.transform = 'scale(1) rotate(0deg)';
                    }
                });
            });

            // Reviews slider - pause on hover/focus
            const reviewsTrack = document.querySelector('.reviews-slider-track');
            if (reviewsTrack) {
                reviewsTrack.addEventListener('mouseenter', () => {
                    reviewsTrack.style.animationPlayState = 'paused';
                });
                
                reviewsTrack.addEventListener('mouseleave', () => {
                    reviewsTrack.style.animationPlayState = 'running';
                });
            }
        });document.addEventListener("DOMContentLoaded", function() {
    const track = document.querySelector('.reviews-slider-track');
    if (!track) return;

    // Get width of the group (original set)
    const group = track.querySelector('.reviews-group-original');
    const groupWidth = group.offsetWidth;

    let pos = 0;
    let speed = 1; // pixels per frame (adjust for faster/slower)
    let animationFrame;

    function slide() {
        pos -= speed;
        // If we've scrolled an entire group, reset for infinite effect
        if (Math.abs(pos) >= groupWidth) {
            pos = 0;
        }
        track.style.transform = `translateX(${pos}px)`;
        animationFrame = requestAnimationFrame(slide);
    }

    // Start sliding
    slide();

    // Pause on hover
    track.addEventListener('mouseenter', function() {
        cancelAnimationFrame(animationFrame);
    });
    track.addEventListener('mouseleave', function() {
        slide();
    });
});

    </script>
