<!-- resources/views/components/floating-chatbot.blade.php -->
<div id="floating-chatbot">
    <!-- Chatbot Toggle Button -->
    <button id="chatbot-toggle" class="chatbot-toggle" aria-label="Toggle Chatbot">
        <svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg" class="robot-icon">
            <ellipse cx="310" cy="420" rx="70" ry="85" fill="url(#bodyGradient)"/>
            <circle cx="310" cy="310" r="85" fill="url(#headGradient)"/>
            <circle cx="335" cy="225" r="8" fill="#B8F090"/>
            <line x1="335" y1="225" x2="335" y2="175" stroke="#B8F090" stroke-width="5" stroke-linecap="round"/>
            <circle cx="335" cy="165" r="10" fill="#B8F090"/>
            <ellipse cx="310" cy="295" rx="70" ry="38" fill="#1A3D4D"/>
            <circle cx="285" cy="290" r="18" fill="#FFF"/>
            <circle cx="285" cy="290" r="10" fill="#1A3D4D" stroke="#FFF" stroke-width="2"/>
            <circle cx="281" cy="286" r="4" fill="#FFF"/>
            <circle cx="335" cy="290" r="18" fill="#FFF"/>
            <circle cx="335" cy="290" r="10" fill="#1A3D4D" stroke="#FFF" stroke-width="2"/>
            <circle cx="331" cy="286" r="4" fill="#FFF"/>
            <path d="M 295 320 Q 310 328 325 320" stroke="#FFF" stroke-width="5" fill="none" stroke-linecap="round"/>
            <defs>
                <linearGradient id="headGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                    <stop offset="0%" style="stop-color:#00E5D0;stop-opacity:1" />
                    <stop offset="100%" style="stop-color:#90F070;stop-opacity:1" />
                </linearGradient>
                <linearGradient id="bodyGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                    <stop offset="0%" style="stop-color:#00D4C5;stop-opacity:1" />
                    <stop offset="100%" style="stop-color:#80E060;stop-opacity:1" />
                </linearGradient>
            </defs>
        </svg>
    </button>

    <!-- Chatbot Window -->
    <div id="chatbot-window" class="chatbot-window">
        <!-- Header -->
        <div class="chatbot-header">
            <div class="chatbot-header-content">
                <div class="robot-avatar">
                    <svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg">
                        <circle cx="310" cy="310" r="85" fill="url(#headerRobotGradient)"/>
                        <ellipse cx="310" cy="295" rx="70" ry="38" fill="#1A3D4D"/>
                        <circle cx="285" cy="290" r="18" fill="#FFF"/>
                        <circle cx="285" cy="290" r="10" fill="#1A3D4D" stroke="#FFF" stroke-width="2"/>
                        <circle cx="335" cy="290" r="18" fill="#FFF"/>
                        <circle cx="335" cy="290" r="10" fill="#1A3D4D" stroke="#FFF" stroke-width="2"/>
                        <path d="M 295 320 Q 310 328 325 320" stroke="#FFF" stroke-width="5" fill="none" stroke-linecap="round"/>
                        <defs>
                            <linearGradient id="headerRobotGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                                <stop offset="0%" style="stop-color:#00E5D0;stop-opacity:1" />
                                <stop offset="100%" style="stop-color:#90F070;stop-opacity:1" />
                            </linearGradient>
                        </defs>
                    </svg>
                </div>
                <div class="chatbot-title">
                    <h3>AI Assistant</h3>
                    <span class="status-online">Online</span>
                </div>
            </div>
            <button id="chatbot-close" class="chatbot-close" aria-label="Close Chatbot">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <line x1="18" y1="6" x2="6" y2="18"></line>
                    <line x1="6" y1="6" x2="18" y2="18"></line>
                </svg>
            </button>
        </div>

        <!-- Chat Messages -->
        <div id="chatbot-messages" class="chatbot-messages">
            <div class="message bot-message">
                <div class="message-avatar">
                    <svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg">
                        <circle cx="310" cy="310" r="85" fill="url(#msgRobotGradient)"/>
                        <ellipse cx="310" cy="295" rx="70" ry="38" fill="#1A3D4D"/>
                        <circle cx="285" cy="290" r="18" fill="#FFF"/>
                        <circle cx="285" cy="290" r="10" fill="#1A3D4D"/>
                        <circle cx="335" cy="290" r="18" fill="#FFF"/>
                        <circle cx="335" cy="290" r="10" fill="#1A3D4D"/>
                        <path d="M 295 320 Q 310 328 325 320" stroke="#FFF" stroke-width="4" fill="none"/>
                        <defs>
                            <linearGradient id="msgRobotGradient" x1="0%" y1="0%" x2="100%" y2="100%">
                                <stop offset="0%" style="stop-color:#00E5D0;stop-opacity:1" />
                                <stop offset="100%" style="stop-color:#90F070;stop-opacity:1" />
                            </linearGradient>
                        </defs>
                    </svg>
                </div>
                <div class="message-content">
                    <p>Hello! ðŸ‘‹ How can I assist you today?</p>
                </div>
            </div>
        </div>

        <!-- Quick Actions -->
        <div class="chatbot-quick-actions">
            <button class="quick-action-btn" data-message="What services do you offer?">
                Services
            </button>
            <button class="quick-action-btn" data-message="Tell me about your products">
                Products
            </button>
            <button class="quick-action-btn" data-message="Contact information">
                Contact
            </button>
        </div>

        <!-- Input Area -->
        <div class="chatbot-input-area">
            <input 
                type="text" 
                id="chatbot-input" 
                placeholder="Type your message..." 
                autocomplete="off"
            />
            <button id="chatbot-send" class="chatbot-send-btn" aria-label="Send Message">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                    <line x1="22" y1="2" x2="11" y2="13"></line>
                    <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
                </svg>
            </button>
        </div>
    </div>
</div>

<style>
/* Floating Chatbot Styles */
#floating-chatbot {
    position: fixed;
    bottom: 20px;
    right: 20px;
    z-index: 9999;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
}

/* Toggle Button */
.chatbot-toggle {
    width: 70px;
    height: 70px;
    border-radius: 50%;
    background: linear-gradient(135deg, #00E5D0 0%, #90F070 100%);
    border: none;
    cursor: pointer;
    box-shadow: 0 4px 20px rgba(0, 229, 208, 0.4);
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 10px;
}

.chatbot-toggle:hover {
    transform: scale(1.1);
    box-shadow: 0 6px 30px rgba(0, 229, 208, 0.6);
}

.chatbot-toggle:active {
    transform: scale(0.95);
}

.robot-icon {
    width: 100%;
    height: 100%;
}

/* Chatbot Window */
.chatbot-window {
    position: absolute;
    bottom: 90px;
    right: 0;
    width: 380px;
    height: 550px;
    background: #fff;
    border-radius: 20px;
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.15);
    display: none;
    flex-direction: column;
    overflow: hidden;
    animation: slideUp 0.3s ease;
}

.chatbot-window.active {
    display: flex;
}

@keyframes slideUp {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Header */
.chatbot-header {
    background: linear-gradient(135deg, #00E5D0 0%, #90F070 100%);
    padding: 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    color: #fff;
}

.chatbot-header-content {
    display: flex;
    align-items: center;
    gap: 12px;
}

.robot-avatar {
    width: 50px;
    height: 50px;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    padding: 8px;
}

.robot-avatar svg {
    width: 100%;
    height: 100%;
}

.chatbot-title h3 {
    margin: 0;
    font-size: 18px;
    font-weight: 600;
}

.status-online {
    font-size: 12px;
    opacity: 0.9;
}

.chatbot-close {
    background: rgba(255, 255, 255, 0.2);
    border: none;
    color: #fff;
    width: 32px;
    height: 32px;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.chatbot-close:hover {
    background: rgba(255, 255, 255, 0.3);
}

/* Messages Area */
.chatbot-messages {
    flex: 1;
    overflow-y: auto;
    padding: 20px;
    background: #f8f9fa;
    display: flex;
    flex-direction: column;
    gap: 15px;
}

.chatbot-messages::-webkit-scrollbar {
    width: 6px;
}

.chatbot-messages::-webkit-scrollbar-track {
    background: #f1f1f1;
}

.chatbot-messages::-webkit-scrollbar-thumb {
    background: #00D9C5;
    border-radius: 3px;
}

.message {
    display: flex;
    gap: 10px;
    animation: fadeIn 0.3s ease;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.message-avatar {
    width: 32px;
    height: 32px;
    flex-shrink: 0;
}

.message-avatar svg {
    width: 100%;
    height: 100%;
}

.message-content {
    background: #fff;
    padding: 12px 16px;
    border-radius: 18px;
    max-width: 70%;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.05);
}

.message-content p {
    margin: 0;
    color: #333;
    font-size: 14px;
    line-height: 1.5;
}

.user-message {
    flex-direction: row-reverse;
}

.user-message .message-content {
    background: linear-gradient(135deg, #00E5D0 0%, #90F070 100%);
    color: #fff;
}

.user-message .message-content p {
    color: #fff;
}

.bot-message .message-avatar {
    background: linear-gradient(135deg, #00E5D0 0%, #90F070 100%);
    border-radius: 50%;
    padding: 4px;
}

/* Typing indicator */
.typing-dots {
    display: flex;
    gap: 4px;
    padding: 8px 0;
}

.typing-dots span {
    width: 8px;
    height: 8px;
    background: #00D9C5;
    border-radius: 50%;
    animation: typing 1.4s infinite;
}

.typing-dots span:nth-child(2) {
    animation-delay: 0.2s;
}

.typing-dots span:nth-child(3) {
    animation-delay: 0.4s;
}

@keyframes typing {
    0%, 60%, 100% {
        transform: translateY(0);
        opacity: 0.7;
    }
    30% {
        transform: translateY(-10px);
        opacity: 1;
    }
}

/* Quick Actions */
.chatbot-quick-actions {
    padding: 15px 20px;
    background: #fff;
    border-top: 1px solid #e9ecef;
    display: flex;
    gap: 8px;
    flex-wrap: wrap;
}

.quick-action-btn {
    padding: 8px 16px;
    background: #f0f0f0;
    border: 1px solid #e0e0e0;
    border-radius: 20px;
    font-size: 13px;
    cursor: pointer;
    transition: all 0.2s ease;
    color: #333;
}

.quick-action-btn:hover {
    background: linear-gradient(135deg, #00E5D0 0%, #90F070 100%);
    color: #fff;
    border-color: transparent;
}

/* Input Area */
.chatbot-input-area {
    padding: 15px 20px;
    background: #fff;
    border-top: 1px solid #e9ecef;
    display: flex;
    gap: 10px;
}

#chatbot-input {
    flex: 1;
    border: 1px solid #e0e0e0;
    border-radius: 25px;
    padding: 12px 20px;
    font-size: 14px;
    outline: none;
    transition: border-color 0.2s ease;
}

#chatbot-input:focus {
    border-color: #00D9C5;
}

.chatbot-send-btn {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    background: linear-gradient(135deg, #00E5D0 0%, #90F070 100%);
    border: none;
    color: #fff;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.2s ease;
}

.chatbot-send-btn:hover {
    transform: scale(1.05);
    box-shadow: 0 4px 15px rgba(0, 229, 208, 0.4);
}

.chatbot-send-btn:active {
    transform: scale(0.95);
}

/* Responsive */
@media (max-width: 480px) {
    .chatbot-window {
        width: calc(100vw - 40px);
        height: calc(100vh - 120px);
        bottom: 90px;
        right: 20px;
    }
}
</style>

<script>
document.addEventListener('DOMContentLoaded', function() {
    const chatbotToggle = document.getElementById('chatbot-toggle');
    const chatbotWindow = document.getElementById('chatbot-window');
    const chatbotClose = document.getElementById('chatbot-close');
    const chatbotInput = document.getElementById('chatbot-input');
    const chatbotSend = document.getElementById('chatbot-send');
    const chatbotMessages = document.getElementById('chatbot-messages');
    const quickActionBtns = document.querySelectorAll('.quick-action-btn');

    let conversationHistory = [];
    let isProcessing = false;

    // Toggle chatbot window
    chatbotToggle.addEventListener('click', function() {
        chatbotWindow.classList.toggle('active');
        if (chatbotWindow.classList.contains('active')) {
            chatbotInput.focus();
        }
    });

    // Close chatbot
    chatbotClose.addEventListener('click', function() {
        chatbotWindow.classList.remove('active');
    });

    // Send message
    function sendMessage() {
        const message = chatbotInput.value.trim();
        if (message === '' || isProcessing) return;

        isProcessing = true;
        
        // Add user message
        addMessage(message, 'user');
        conversationHistory.push({ role: 'user', content: message });
        chatbotInput.value = '';

        // Show typing indicator
        const typingId = addTypingIndicator();

        // Call API
        fetch('/api/chat', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            },
            body: JSON.stringify({
                message: message,
                history: conversationHistory
            })
        })
        .then(response => response.json())
        .then(data => {
            removeTypingIndicator(typingId);
            
            if (data.success && data.response) {
                addMessage(data.response, 'bot');
                conversationHistory.push({ role: 'assistant', content: data.response });
            } else {
                addMessage('Sorry, I couldn\'t process your request. Please try again.', 'bot');
            }
            
            isProcessing = false;
        })
        .catch(error => {
            console.error('Error:', error);
            removeTypingIndicator(typingId);
            addMessage('Sorry, something went wrong. Please check your connection.', 'bot');
            isProcessing = false;
        });
    }

    chatbotSend.addEventListener('click', sendMessage);
    chatbotInput.addEventListener('keypress', function(e) {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            sendMessage();
        }
    });

    // Quick actions
    quickActionBtns.forEach(btn => {
        btn.addEventListener('click', function() {
            const message = this.getAttribute('data-message');
            chatbotInput.value = message;
            sendMessage();
        });
    });

    // Add message to chat
    function addMessage(text, type) {
        const messageDiv = document.createElement('div');
        messageDiv.className = `message ${type}-message`;

        if (type === 'bot') {
            messageDiv.innerHTML = `
                <div class="message-avatar">
                    ${getRobotSVG()}
                </div>
                <div class="message-content">
                    <p>${escapeHtml(text)}</p>
                </div>
            `;
        } else {
            messageDiv.innerHTML = `
                <div class="message-content">
                    <p>${escapeHtml(text)}</p>
                </div>
            `;
        }

        chatbotMessages.appendChild(messageDiv);
        chatbotMessages.scrollTop = chatbotMessages.scrollHeight;
    }

    // Typing indicator
    function addTypingIndicator() {
        const id = 'typing-' + Date.now();
        const typingDiv = document.createElement('div');
        typingDiv.className = 'message bot-message';
        typingDiv.id = id;
        typingDiv.innerHTML = `
            <div class="message-avatar">
                ${getRobotSVG()}
            </div>
            <div class="message-content">
                <div class="typing-dots">
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
            </div>
        `;
        chatbotMessages.appendChild(typingDiv);
        chatbotMessages.scrollTop = chatbotMessages.scrollHeight;
        return id;
    }

    function removeTypingIndicator(id) {
        const indicator = document.getElementById(id);
        if (indicator) {
            indicator.remove();
        }
    }

    // Helper functions
    function escapeHtml(text) {
        const div = document.createElement('div');
        div.textContent = text;
        return div.innerHTML;
    }

    function getRobotSVG() {
        return `<svg viewBox="0 0 600 600" xmlns="http://www.w3.org/2000/svg">
            <circle cx="310" cy="310" r="85" fill="url(#msgRobotGradient${Date.now()})"/>
            <ellipse cx="310" cy="295" rx="70" ry="38" fill="#1A3D4D"/>
            <circle cx="285" cy="290" r="18" fill="#FFF"/>
            <circle cx="285" cy="290" r="10" fill="#1A3D4D"/>
            <circle cx="335" cy="290" r="18" fill="#FFF"/>
            <circle cx="335" cy="290" r="10" fill="#1A3D4D"/>
            <path d="M 295 320 Q 310 328 325 320" stroke="#FFF" stroke-width="4" fill="none"/>
            <defs>
                <linearGradient id="msgRobotGradient${Date.now()}" x1="0%" y1="0%" x2="100%" y2="100%">
                    <stop offset="0%" style="stop-color:#00E5D0;stop-opacity:1" />
                    <stop offset="100%" style="stop-color:#90F070;stop-opacity:1" />
                </linearGradient>
            </defs>
        </svg>`;
    }
});
</script>
