    {{-- ===== JS (single, tidy, no Blade conflicts) ===== --}}
    <script defer>
        // public function for product navigation
        function redirectToProduct(productLink) { window.location.href = productLink; }

        document.addEventListener("DOMContentLoaded", () => {
            // Logos marquee
            const logoTrack = document.querySelector(".logos-track");
            if (logoTrack) {
                let speed = 1, position = 0;
                const loop = () => {
                    position -= speed;
                    if (Math.abs(position) >= logoTrack.scrollWidth / 2) position = 0;
                    logoTrack.style.transform = `translateX(${position}px)`;
                    requestAnimationFrame(loop);
                };
                loop();
            }

            // Industries marquee
            const casesTrack = document.querySelector(".cases-track");
            if (casesTrack) {
                let caseSpeed = 1, casePos = 0;
                const loopCases = () => {
                    casePos -= caseSpeed;
                    if (Math.abs(casePos) >= casesTrack.scrollWidth / 2) casePos = 0;
                    casesTrack.style.transform = `translateX(${casePos}px)`;
                    requestAnimationFrame(loopCases);
                };
                loopCases();
            }

            // Enhance product thumbnails for a11y + feedback
            const thumbs = document.querySelectorAll('.thumb');
            thumbs.forEach(thumb => {
                if (thumb.tagName !== 'BUTTON') { thumb.setAttribute('role', 'button'); thumb.setAttribute('tabindex', '0'); }
                const label = thumb.dataset.product ? `Navigate to ${thumb.dataset.product} page` : 'Open product';
                thumb.setAttribute('aria-label', label);

                thumb.addEventListener('keydown', (e) => {
                    if (e.key === 'Enter' || e.key === ' ') { e.preventDefault(); thumb.click(); }
                });
                thumb.addEventListener('mouseenter', function () { this.style.transform = 'translateY(-4px) scale(1.02)'; this.style.boxShadow = '0 15px 35px rgba(0,0,0,.15)'; });
                thumb.addEventListener('mouseleave', function () { this.style.transform = 'translateY(0) scale(1)'; this.style.boxShadow = '0 10px 25px rgba(0,0,0,.1)'; });
                thumb.addEventListener('mousedown', function () { this.style.transform = 'translateY(-2px) scale(.98)'; });
                thumb.addEventListener('mouseup', function () { this.style.transform = 'translateY(-4px) scale(1.02)'; });
            });

            // Image load/error states
            const allImgs = document.querySelectorAll('img');
            allImgs.forEach(img => {
                img.addEventListener('load', function () { this.style.opacity = '1'; }, { once: true });
                img.addEventListener('error', function () { this.style.opacity = '0.5'; if (!this.alt) this.alt = 'Image not available'; }, { once: true });
            });
        });
    </script>
