    {{-- Page script (Blade-safe; no @ directives inside strings) --}}
    <script defer>
        document.addEventListener('DOMContentLoaded', function () {
            // Simple AOS-like reveal
const reveal = () => {
                document.querySelectorAll('[data-aos]').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.top < window.innerHeight - 120) el.classList.add('aos-animate');
                });
            };
            reveal();
            window.addEventListener('scroll', reveal, { passive: true });

            // Read more / less
            document.querySelectorAll('.read-more').forEach(btn => {
                btn.addEventListener('click', function (e) {
                    e.preventDefault();
                    const parentItem = this.closest('.timeline-item');
                    const expandable = parentItem.querySelector('.expandable-text');

                    const isHidden = expandable.hasAttribute('hidden');
                    if (isHidden) {
                        expandable.removeAttribute('hidden');
                        this.textContent = 'Read less';
                        this.setAttribute('aria-expanded', 'true');
                        parentItem.querySelector('.timeline-card').classList.add('expanded');
                    } else {
                        expandable.setAttribute('hidden', '');
                        this.textContent = 'Read more';
                        this.setAttribute('aria-expanded', 'false');
                        parentItem.querySelector('.timeline-card').classList.remove('expanded');
                    }
                });
            });

            // Smooth anchor on load
             if (location.hash) {
  const el = document.querySelector(location.hash);
  if (el) {
    setTimeout(() => {
      const headerOffset = 150; // Set to your header's height in px
const elementPosition = el.getBoundingClientRect().top + window.pageYOffset;
window.scrollTo({
  top: elementPosition - headerOffset,
  behavior: "smooth"
});

      el.classList.add("highlight");
      setTimeout(() => el.classList.remove("highlight"), 1500);
    }, 120);
  }
}

        });
    </script>
