     // Blockchain Features Platform Section JavaScript - Fixed Image Position
document.addEventListener('DOMContentLoaded', function() {
    setTimeout(() => {
                initializeBlockchainFeatures();
            }, 300);
});

 function initializeBlockchainFeatures(){
            const featuresSection = document.querySelector('.cyber-features-platform');
            const scrollContainer = document.querySelector('.features-scroll-container');

            if (!featuresSection || !scrollContainer) return;

            initializeHeadingAnimation();
            initializeFeatureScrollAnimation();
            addSmoothBehavior();
        }
     function initializeHeadingAnimation(){
            const heading = document.querySelector('.platform-title');
            const subtitle = document.querySelector('.platform-subtitle');

            if(!heading || !subtitle) return;

            const observer = new IntersectionObserver(entries => {
                entries.forEach(entry => {
                    if(entry.isIntersecting){
                        setTimeout(() => heading.classList.add('animate'), 200);
                        setTimeout(() => subtitle.classList.add('animate'), 600);
                        setTimeout(() => {
                            const firstBlock = document.querySelector('.feature-block:first-child');
                            firstBlock?.classList.add('animate');
                        }, 1000);
                        observer.unobserve(entry.target);
                    }
                });
            }, {threshold: 0.3, rootMargin: '0px 0px -100px 0px'});

            observer.observe(heading);
        }

function showFirstFeatureBlock() {
    const firstBlock = document.querySelector('.feature-block:first-child');
    if (firstBlock) {
        firstBlock.classList.add('animate');
    }
}

 function initializeFeatureScrollAnimation(){
            const container = document.querySelector('.features-scroll-container');
            const blocks = document.querySelectorAll('.feature-block');
            if(!container || blocks.length === 0) return;

            const observer = new IntersectionObserver(entries => {
                entries.forEach(entry => {
                    if(entry.isIntersecting){
                        const anim = entry.target.classList.contains('slide-right') ? 'slide-right' : 'slide-left';
                        const delay = anim === 'slide-right' ? 300 : 150;
                        setTimeout(() => {
                            entry.target.classList.add('animate');
                        }, delay);
                        observer.unobserve(entry.target);
                    }
                });
            },{
                root: container,
                threshold: 0.4,
                rootMargin: '0px 0px -50px 0px'
            });

            blocks.forEach((block, idx) => idx > 0 && observer.observe(block));
        }

function animateProgressBars() {
    const progressFills = document.querySelectorAll('.progress-fill');

    progressFills.forEach((fill, index) => {
        setTimeout(() => {
            const targetWidth = fill.style.width || '0%';
            fill.style.width = '0%';

            setTimeout(() => {
                fill.style.width = targetWidth;
            }, 100);
        }, index * 200);
    });
}

   function addSmoothBehavior(){
            const container = document.querySelector('.features-scroll-container');
            if(!container) return;

            const blocks = document.querySelectorAll('.feature-block');
            blocks.forEach(block => {
                block.addEventListener('mouseenter', () => {
                    block.style.transform = 'translateY(-5px)';
                    block.style.transition = 'transform 0.3s ease';
                });
                block.addEventListener('mouseleave', () => {
                    block.style.transform = 'translateY(0)';
                });
            });
        }

function throttle(func, limit) {
    let inThrottle;
    return function() {
        const args = arguments;
        const context = this;
        if (!inThrottle) {
            func.apply(context, args);
            inThrottle = true;
            setTimeout(() => inThrottle = false, limit);
        }
    }
}

    </script>

</body>
</html>
