    <script  defer>
       document.addEventListener("DOMContentLoaded", () => {
            const particles = document.querySelectorAll('.particle');
            particles.forEach((p, i) => { p.style.animationDelay = `${i * 0.5}s`; });

            const form = document.getElementById('securityForm');
            form?.addEventListener('submit', e => {
                e.preventDefault();
                alert('Security assessment request submitted successfully!');
            });

            setTimeout(() => {
                initializeCyberFeaturesSection();
            }, 300);
        });

        // Cyber Security Features Platform Section JavaScript
        document.addEventListener('DOMContentLoaded', function() {
            setTimeout(() => {
                initializeCyberFeaturesSection();
            }, 300);
        });

       function initializeCyberFeaturesSection() {
            const featuresSection = document.querySelector('.cyber-features-platform');
            const scrollContainer = document.querySelector('.features-scroll-container');
            if (!featuresSection || !scrollContainer) return;
            initializeHeadingAnimation();
            initializeFeatureScrollAnimation();
            addSmoothScrollBehavior();
        }

        // Same animation functions as AI version but adapted for cyber security
       // Same JavaScript functions as previous templates for animations
        function initializeFeaturesSection() {
            const featuresSection = document.querySelector('.cyber-features-platform');
            const scrollContainer = document.querySelector('.features-scroll-container');

            if (!featuresSection || !scrollContainer) return;

            initializeHeadingAnimation();
            initializeFeatureScrollAnimation();
            addSmoothScrollBehavior();
        }

     function initializeHeadingAnimation() {
            const title = document.querySelector('.platform-title');
            const subtitle = document.querySelector('.platform-subtitle');
            if (!title || !subtitle) return;

            const observer = new IntersectionObserver(entries => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        setTimeout(() => title.classList.add('animate'), 200);
                        setTimeout(() => subtitle.classList.add('animate'), 600);
                        setTimeout(() => {
                            const firstBlock = document.querySelector('.feature-block:first-child');
                            firstBlock?.classList.add('animate');
                        }, 1000);
                        observer.unobserve(entry.target);
                    }
                });
            }, { threshold: 0.3, rootMargin: '0px 0px -100px 0px' });

            observer.observe(title);
        }

          function initializeFeatureScrollAnimation() {
            const container = document.querySelector('.features-scroll-container');
            const blocks = document.querySelectorAll('.feature-block');
            if (!container || blocks.length === 0) return;

            const observer = new IntersectionObserver(entries => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        const animClass = entry.target.classList.contains('feature-right') ? 'slide-right' : 'slide-left';
                        const delay = animClass === 'slide-right' ? 300 : 150;
                        setTimeout(() => { entry.target.classList.add('animate'); }, delay);
                        observer.unobserve(entry.target);
                    }
                });
            }, { root: container, threshold: 0.4, rootMargin: '0px 0px -50px 0px' });

            blocks.forEach((block, idx) => { if (idx > 0) observer.observe(block); });
        }

        function addSmoothScrollBehavior() {
            const container = document.querySelector('.features-scroll-container');
            if (!container) return;
            const blocks = document.querySelectorAll('.feature-block');
            blocks.forEach(block => {
                block.addEventListener('mouseenter', () => {
                    block.style.transform = 'translateY(-5px)';
                    block.style.transition = 'transform 0.3s ease';
                });
                block.addEventListener('mouseleave', () => {
                    block.style.transform = 'translateY(0)';
                });
            });
        }
    </script>

</body>
</html>