    <script   defer>
        document.addEventListener("DOMContentLoaded", () => {
            // Animate particles
            const particles = document.querySelectorAll('.particle');
            particles.forEach((particle, index) => {
                particle.style.animationDelay = `${index * 0.5}s`;
            });

            // Form submission
            const form = document.getElementById('dataForm');
            form.addEventListener('submit', (e) => {
                e.preventDefault();
                alert('Data science consultation request submitted successfully!');
            });
        });

        // Data Science Features Platform Section JavaScript
        document.addEventListener('DOMContentLoaded', function() {
            setTimeout(() => {
                initializeDataFeaturesSection();
            }, 300);
        });

        function initializeDataFeaturesSection() {
            const featuresSection = document.querySelector('.cyber-features-platform');
            const scrollContainer = document.querySelector('.features-scroll-container');

            if (!featuresSection || !scrollContainer) return;

            initializeHeadingAnimation();
            initializeFeatureScrollAnimation();
            addSmoothScrollBehavior();
        }

        function initializeHeadingAnimation() {
            const titleElement = document.querySelector('.platform-title');
            const subtitleElement = document.querySelector('.platform-subtitle');

            if (!titleElement || !subtitleElement) return;

            const headingObserver = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        setTimeout(() => {
                            entry.target.classList.add('animate');
                        }, 200);

                        setTimeout(() => {
                            subtitleElement.classList.add('animate');
                        }, 600);

                        setTimeout(() => {
                            showFirstFeatureBlock();
                        }, 1000);

                        headingObserver.unobserve(entry.target);
                    }
                });
            }, {
                threshold: 0.3,
                rootMargin: '0px 0px -100px 0px'
            });

            headingObserver.observe(titleElement);
        }

        function showFirstFeatureBlock() {
            const firstBlock = document.querySelector('.feature-block:first-child');
            if (firstBlock) {
                firstBlock.classList.add('animate');
            }
        }

        function initializeFeatureScrollAnimation() {
            const scrollContainer = document.querySelector('.features-scroll-container');
            const featureBlocks = document.querySelectorAll('.feature-block');

            if (!scrollContainer || featureBlocks.length === 0) return;

            const featureObserver = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        const animationType = entry.target.classList.contains('feature-right') ? 'slide-right' : 'slide-left';
                        const delay = animationType === 'slide-right' ? 300 : 150;

                        setTimeout(() => {
                            entry.target.classList.add('animate');

                            if (entry.target.classList.contains('feature-right')) {
                                animateProgressBars();
                            }
                        }, delay);

                        featureObserver.unobserve(entry.target);
                    }
                });
            }, {
                root: scrollContainer,
                threshold: 0.4,
                rootMargin: '0px 0px -50px 0px'
            });

            featureBlocks.forEach((block, index) => {
                if (index > 0) {
                    featureObserver.observe(block);
                }
            });
        }

        function animateProgressBars() {
            const progressFills = document.querySelectorAll('.progress-fill');

            progressFills.forEach((fill, index) => {
                setTimeout(() => {
                    const targetWidth = fill.style.width || '0%';
                    fill.style.width = '0%';

                    setTimeout(() => {
                        fill.style.width = targetWidth;
                    }, 100);
                }, index * 200);
            });
        }

        function addSmoothScrollBehavior() {
            const scrollContainer = document.querySelector('.features-scroll-container');
            if (!scrollContainer) return;

            scrollContainer.addEventListener('scroll', throttle(() => {
                const scrollTop = scrollContainer.scrollTop;
                const images = document.querySelectorAll('.feature-image img');

                images.forEach((img, index) => {
                    const speed = (index % 2 === 0) ? 0.5 : -0.3;
                    const yPos = scrollTop * speed;
                    img.style.transform = `translateY(${yPos}px) scale(1)`;
                });

                const overlayElements = document.querySelectorAll('.security-badge, .compliance-badge, .revenue-chart');
                overlayElements.forEach((element, index) => {
                    const speed = 0.2 + (index * 0.1);
                    const yPos = Math.sin(scrollTop * 0.01 + index) * speed;
                    element.style.transform = `translateY(${yPos}px)`;
                });
            }, 16));

            const featureBlocks = document.querySelectorAll('.feature-block');
            featureBlocks.forEach(block => {
                block.addEventListener('mouseenter', () => {
                    block.style.transform = 'translateY(-5px)';
                    block.style.transition = 'transform 0.3s ease';
                });

                block.addEventListener('mouseleave', () => {
                    block.style.transform = 'translateY(0)';
                });
            });
        }

        function throttle(func, limit) {
            let inThrottle;
            return function() {
                const args = arguments;
                const context = this;
                if (!inThrottle) {
                    func.apply(context, args);
                    inThrottle = true;
                    setTimeout(() => inThrottle = false, limit);
                }
            }
        }



        
    </script>

</body>
</html>
