        document.addEventListener("DOMContentLoaded", () => {
            // Animate particles
            const particles = document.querySelectorAll('.particle');
            particles.forEach((particle, index) => {
                particle.style.animationDelay = `${index * 0.5}s`;
            });

            // Form submission
            const form = document.getElementById('infrastructureForm');
            form.addEventListener('submit', (e) => {
                e.preventDefault();
                alert('Remote infrastructure assessment request submitted successfully!');
            });

            // Initialize features section animations
            setTimeout(() => {
                initializeFeaturesSection();
            }, 300);

            // Animate dashboard metrics
            animateDashboardMetrics();
        });

        // Animate dashboard metrics with counting effect
        function animateDashboardMetrics() {
            const metricCards = document.querySelectorAll('.metric-card');
            
            const observer = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        const metricValue = entry.target.querySelector('.metric-value');
                        const finalValue = metricValue.textContent;
                        
                        if (finalValue.includes('%')) {
                            animatePercentage(metricValue, finalValue);
                        } else if (finalValue.includes('ms')) {
                            animateMilliseconds(metricValue, finalValue);
                        }
                        
                        observer.unobserve(entry.target);
                    }
                });
            }, { threshold: 0.5 });

            metricCards.forEach(card => observer.observe(card));
        }

        function animatePercentage(element, finalValue) {
            const numericValue = parseFloat(finalValue);
            let currentValue = 0;
            const increment = numericValue / 50;
            
            const animation = setInterval(() => {
                currentValue += increment;
                if (currentValue >= numericValue) {
                    element.textContent = finalValue;
                    clearInterval(animation);
                } else {
                    element.textContent = currentValue.toFixed(2) + '%';
                }
            }, 40);
        }

        function animateMilliseconds(element, finalValue) {
            const numericValue = parseInt(finalValue);
            let currentValue = numericValue * 2;
            const decrement = numericValue / 30;
            
            const animation = setInterval(() => {
                currentValue -= decrement;
                if (currentValue <= numericValue) {
                    element.textContent = finalValue;
                    clearInterval(animation);
                } else {
                    element.textContent = Math.floor(currentValue) + 'ms';
                }
            }, 50);
        }

        // Same JavaScript functions as previous templates for animations
        function initializeFeaturesSection() {
            const featuresSection = document.querySelector('.cyber-features-platform');
            const scrollContainer = document.querySelector('.features-scroll-container');

            if (!featuresSection || !scrollContainer) return;

            initializeHeadingAnimation();
            initializeFeatureScrollAnimation();
            addSmoothScrollBehavior();
        }

        function initializeHeadingAnimation() {
            const titleElement = document.querySelector('.platform-title');
            const subtitleElement = document.querySelector('.platform-subtitle');

            if (!titleElement || !subtitleElement) return;

            const headingObserver = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        setTimeout(() => {
                            entry.target.classList.add('animate');
                        }, 200);

                        setTimeout(() => {
                            subtitleElement.classList.add('animate');
                        }, 600);

                        setTimeout(() => {
                            const firstBlock = document.querySelector('.feature-block:first-child');
                            if (firstBlock) firstBlock.classList.add('animate');
                        }, 1000);

                        headingObserver.unobserve(entry.target);
                    }
                });
            }, { threshold: 0.3, rootMargin: '0px 0px -100px 0px' });

            headingObserver.observe(titleElement);
        }

        function initializeFeatureScrollAnimation() {
            const scrollContainer = document.querySelector('.features-scroll-container');
            const featureBlocks = document.querySelectorAll('.feature-block');

            if (!scrollContainer || featureBlocks.length === 0) return;

            const featureObserver = new IntersectionObserver((entries) => {
                entries.forEach(entry => {
                    if (entry.isIntersecting) {
                        const animationType = entry.target.classList.contains('feature-right') ? 'slide-right' : 'slide-left';
                        const delay = animationType === 'slide-right' ? 300 : 150;

                        setTimeout(() => {
                            entry.target.classList.add('animate');
                        }, delay);

                        featureObserver.unobserve(entry.target);
                    }
                });
            }, {
                root: scrollContainer,
                threshold: 0.4,
                rootMargin: '0px 0px -50px 0px'
            });

            featureBlocks.forEach((block, index) => {
                if (index > 0) featureObserver.observe(block);
            });
        }

        function addSmoothScrollBehavior() {
            const scrollContainer = document.querySelector('.features-scroll-container');
            if (!scrollContainer) return;

            const featureBlocks = document.querySelectorAll('.feature-block');
            featureBlocks.forEach(block => {
                block.addEventListener('mouseenter', () => {
                    block.style.transform = 'translateY(-5px)';
                    block.style.transition = 'transform 0.3s ease';
                });

                block.addEventListener('mouseleave', () => {
                    block.style.transform = 'translateY(0)';
                });
            });
        }
    </script>

</body>
</html>
